func fdate(dateString string) template.HTML {
	if date, err := time.Parse(time.DateTime, dateString); err == nil {
		return template.HTML(date.Format("02 January 2006<br><span style=\"font-size:80%\">15:04:05</span>"))
	}
	return ""
}
